import {Component} from '@angular/core';
import {CommonModule} from '@angular/common';
import { ActivatedRoute } from '@angular/router';
import { HttpClient } from '@angular/common/http';

@Component({
    standalone: true,
    selector: 'app-user-detail',
    imports: [CommonModule],
    templateUrl: './user-detail.component.html',
})
export class UserDetailComponent {
    id!: string;
    user!: User;
    constructor(private route: ActivatedRoute, private http: HttpClient) {}

    ngOnInit(): void {
        this.id = this.route.snapshot.paramMap.get('id')!;
        this.http.get<User>(`https://localhost:44307/api/User/GetUserById/${this.id}`,{
            headers: {
                "x-api-key": "A6F9E3C2D7B81F4E0A9C5D6B2E1F8A7C4D0E9B6F5A3C8D2E1B7F9A4C6E0D5B8A1F2C9E7D6B4A3F5E0C8D2"
            }
        })
        .subscribe(data => {
            this.user = data;
        });
    }
}


interface Permission {
  permission: string;
  permissionId: number;
}
interface Role {
  userRoleMappingId: string;
  permissions: Permission[];
  roleId: number;
  roleName: string;
}
interface MapRole {
  userRoleMappingId: string;
  role: Role;
}
interface UserProfile {
  firstName: string;
  lastName: string;
  age: number;
  profileId: string;
}
interface User {
  id: string;
  username: string;
  userId: string;
  userProfile: UserProfile;
  userRoleMappings: MapRole[];
}
